import React, { useState, useEffect, useRef } from 'react';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

const wordList = ['Meal', 'Exercise', 'Bruin Buddy', 'Meal'];

const AnimatedText = () => {
    const [currWord, setCurrWord] = useState(wordList[0]);

    const index = useRef(0);
    useEffect(() => {
        let interval = null;
        if (true) {
            interval = setInterval(() => {
                index.current++;
                setCurrWord(wordList[index.current]);
                if (index.current === wordList.length - 1) {
                    index.current = 0;
                }
            }, 1200);
        }
        return () => clearInterval(interval);
    });

    return (
        <Typography variant="h5" gutterBottom >
            Find your favorite <Button
                style={{
                    textTransform: 'none',
                    fontSize: '1.00rem',
                    padding: '2px 12px',
                    minWidth: '120px',
                    backgroundColor: "#dc2626",
                    marginLeft: 'auto'        // Adds space to push the rest to the left
                }}
                variant="contained"
                href="/home"
            >
                {currWord}
            </Button>
        </Typography>

    );
};

export default AnimatedText;
